mapper.insert:


INSERT 
INTO	USER_INFO
	(	USER_CODE
	,	USER_ID
	,	USER_PW
	,	USER_ADDRESS
	,	USER_EMAIL
	,	USER_BIRTH
	,	USER_PHONENUMBER
	,	USER_GENDER
	,	USER_NAME
	,	USER_PW_HINT_CODE
	,	USER_PW_HINT_ANSWER
	,	USE_YN
	,	RGDT_DT
	,	UPDT_DT
	) 
VALUES	
	(	('USER_' || LPAD(user_seq.nextval,8,0))
	,	#{userId}
	,	#{userPw}
	,	#{userAddress}
	,	#{userEmail}
	,	#{userBirth}
	,	#{userPhoneNumber}
	,	#{userGender}
	,	#{userName}
	,	#{userPwHintCode}
	,	#{userPwHintAnswer}
	,	'Y'
	,	sysdate
	,	sysdate
	)

mapper.select:

SELECT	USER_ID
FROM	USER_INFO
WHERE	USER_NAME		=	#{userName}
	AND	USER_EMAIL		=	#{userEmail}
	AND	USER_BIRTH		=	#{userBirth}
	AND	USE_YN			=	'Y'


SELECT	USER_CODE		as	userCode
	 ,	USER_EMAIL		as	userEmail
FROM	USER_INFO
WHERE	USER_NAME		=	#{userName}
	AND	USER_ID			=	#{userId}
	AND	USER_BIRTH		=	#{userBirth}
	AND	USE_YN			=	'Y'

mapper.update:

UPDATE	USER_INFO
SET		USER_PW		=	#{userPw}
WHERE	USER_CODE	=	#{userCode}

mapper.select:

SELECT	COUNT(*)
FROM	USER_INFO
WHERE	USER_CODE			=	#{userCode}
	AND	USER_PW_HINT_CODE	=	#{userPwHintCode}
	AND	USER_PW_HINT_ANSWER	=	#{userPwHintAnswer}
	AND	USE_YN				=	'Y'
