mapper.insert:
 
		
			INSERT 
			INTO	USER_MESSAGE
				(	
					MESSAGE_CODE
				,	USER_CODE_FROM
				,	USER_CODE_TO
				,	MESSAGE_TITLE
				,	MESSAGE_DESC
				,	MESSAGE_TIME
				)  
			VALUES	
				(	
					('MESSAGE_' || LPAD(message_seq.nextval,8,0))
				,	(	SELECT USER_CODE
						FROM USER_INFO
						WHERE USER_ID = #{userCodeFrom}
					)
				,	(	SELECT USER_CODE
						FROM USER_INFO
						WHERE USER_ID = #{userCodeTo}
					)
				,	#{messageTitle}
				,	#{messageDesc}
				,	sysdate
				)
