mapper.select:

	SELECT	COUNT(*)
	FROM	STUDY_INFO
	WHERE	STUDY_NAME		=	#{studyName}

mapper.insert:
 
		
			INSERT 
			INTO	STUDY_INFO
				(	STUDY_CODE
				,	STUDY_NAME
				,	STUDY_DESC
				,	STUDY_TOPIC
				,	STUDY_LIMIT
				,	STUDY_RGSTUS_ID
				,	STUDY_AREA
				) 
			VALUES	
				(	#{userCode}
				,	#{studyName}
				,	#{studyDesc}
				,	#{studyTopic}
				,	#{studyLimit}
				,	#{studyRgstusId}
				,	#{studyArea}
				)
