mapper.select:

	SELECT	COUNT(*)
	FROM	STUDY_INFO
	WHERE	STUDY_NAME		=	#{studyName}

mapper.insert:
 
		
       		SELECT	('STUDY_' || LPAD(study_seq.nextval,8,0))	FROM 	DUAL     
   		
   		
		
			INSERT 
			INTO	STUDY_INFO
				(	STUDY_CODE
				,	STUDY_NAME
				,	STUDY_DESC
				,	STUDY_TOPIC
				,	STUDY_LIMIT
				,	STUDY_RGSTUS_ID
				,	STUDY_AREA
				,	USE_YN
				) 
			VALUES	
				(	#{studyCode}
				,	#{studyName}
				,	#{studyDesc}
				,	#{studyTopic}
				,	#{studyLimit}
				,	#{studyRgstusId}
				,	#{studyArea}
				,	'Y'
				)

mapper.select:
SELECT * FROM ( SELECT ROWNUM as RNUM , main.* FROM ( SELECT /*+ ordered use_nl(si ui) INDEX(si MORESTUDY_IDX) */ si.STUDY_CODE as studyCode , si.STUDY_NAME as studyName , si.STUDY_TOPIC as studyTopic , si.STUDY_LIMIT as studyLimit , si.STUDY_AREA as studyArea , ui.USER_NAME as userName , (SELECT COUNT(*) FROM USER_IN_STUDY uis WHERE uis.STUDY_CODE = si.STUDY_CODE) as totalCount FROM STUDY_INFO si, USER_INFO ui WHERE ui.USER_CODE = si.STUDY_RGSTUS_ID AND si.USE_YN = 'Y'
AND si.STUDY_TOPIC = #{searchStudyTopic}
AND si.STUDY_NAME LIKE '%' || #{searchStudyName} || '%'
AND si.STUDY_AREA LIKE '%' || #{searchStudyArea} || '%'
) main WHERE ROWNUM <= #{last} ) WHERE RNUM >= #{first}

mapper.update:

	UPDATE	STUDY_APPLICATION_FORM_USER
	SET		TITLE					=	#{title}
		,	APPLICATION_FORM_DESC	=	#{applicationFormDesc}
		,	UPDT_DT					=	sysdate
	WHERE	APPLICATION_FORM_CODE	=	#{applicationFormCode}
		AND	USER_CODE				=	#{userCode}

mapper.select:

	SELECT	COUNT(*)
	FROM	STUDY_APPLICATION_FORM_USER
	WHERE	USER_CODE		=	#{userCode}
		AND	STUDY_CODE		=	#{studyCode}
		AND	USE_YN			=	'Y'


	SELECT	COUNT(*)
	FROM	USER_IN_STUDY
	WHERE	USER_CODE	=	#{userCode}
		AND	STUDY_CODE	=	#{studyCode}
		AND	USE_YN		=	'Y'
