mapper.select:

	SELECT	COUNT(*)
	FROM	STUDY_INFO
	WHERE	STUDY_NAME		=	#{studyName}

mapper.insert:
 
		
			INSERT 
			INTO	STUDY_INFO
				(	STUDY_CODE
				,	STUDY_NAME
				,	STUDY_DESC
				,	STUDY_TOPIC
				,	STUDY_LIMIT
				,	STUDY_RGSTUS_ID
				,	STUDY_AREA
				,	USE_YN
				) 
			VALUES	
				(	('STUDY_' || LPAD(study_seq.nextval,8,0))
				,	#{studyName}
				,	#{studyDesc}
				,	#{studyTopic}
				,	#{studyLimit}
				,	#{studyRgstusId}
				,	#{studyArea}
				,	'Y'
				)

mapper.update:

	UPDATE	STUDY_APPLICATION_FORM_USER
	SET		TITLE					=	#{title}
		,	APPLICATION_FORM_DESC	=	#{applicationFormDesc}
		,	UPDT_DT					=	sysdate
	WHERE	APPLICATION_FORM_CODE	=	#{applicationFormCode}
		AND	USER_CODE				=	#{userCode}

mapper.select:

	SELECT	COUNT(*)
	FROM	STUDY_APPLICATION_FORM_USER
	WHERE	USER_CODE		=	#{userCode}
		AND	STUDY_CODE		=	#{studyCode}
		AND	USE_YN			=	'Y'
