mapper.select:
SELECT * FROM ( SELECT ROWNUM as RNUM , main.* FROM ( SELECT /*+ ordered use_nl(us) INDEX_DESC(us USERINSTUDY_IDX) */ us.USER_CODE as userCode , ( SELECT ui.USER_ID FROM USER_INFO ui WHERE ui.USER_CODE = us.USER_CODE) as userId , ( SELECT ui.USER_NAME FROM USER_INFO ui WHERE ui.USER_CODE = us.USER_CODE) as userName , ( SELECT ui.USER_ADDRESS FROM USER_INFO ui WHERE ui.USER_CODE = us.USER_CODE) as userAddress , us.USER_POSITION as studyAuthority FROM USER_IN_STUDY us WHERE us.STUDY_CODE = #{studyCode}
AND us.USER_CODE IN (SELECT ui2.USER_CODE FROM USER_INFO ui2 WHERE ui2.USE_YN = 'Y' AND ui2.USER_ID LIKE '%' || #{searchStudyMemberId} || '%' )
AND us.USER_CODE IN (SELECT ui2.USER_CODE FROM USER_INFO ui2 WHERE ui2.USE_YN = 'Y' AND ui2.USER_NAME LIKE '%' || #{searchStudyMemberName} || '%' )
AND us.USER_CODE IN (SELECT ui2.USER_CODE FROM USER_INFO ui2 WHERE ui2.USE_YN = 'Y' AND ui2.USER_ADDRESS LIKE '%' || #{searchStudyMemberAddress} || '%' )
AND us.USER_POSITION = #{searchStudyMemberIsAdmin}
) main WHERE ROWNUM <= #{last} ) WHERE RNUM >= #{first}

SELECT COUNT(*) FROM USER_IN_STUDY WHERE USER_IN_STUDY.STUDY_CODE = #{studyCode}
AND USER_IN_STUDY.USER_CODE IN (SELECT USER_INFO.USER_CODE FROM USER_INFO WHERE USER_INFO.USE_YN = 'Y' AND USER_INFO.USER_ID LIKE '%' || #{searchStudyMemberId} || '%' )
AND USER_IN_STUDY.USER_CODE IN (SELECT USER_INFO.USER_CODE FROM USER_INFO WHERE USER_INFO.USE_YN = 'Y' AND USER_INFO.USER_NAME LIKE '%' || #{searchStudyMemberName} || '%' )
AND USER_IN_STUDY.USER_CODE IN (SELECT USER_INFO.USER_CODE FROM USER_INFO WHERE USER_INFO.USE_YN = 'Y' AND USER_INFO.USER_ADDRESS LIKE '%' || #{searchStudyMemberAddress} || '%' )
AND USER_IN_STUDY.USER_CODE = #{searchStudyMemberIsAdmin}

SELECT * FROM ( SELECT ROWNUM as RNUM , main.* FROM ( SELECT /*+ ordered use_nl(bt) INDEX_DESC(bt BOARD_IDX ) */ bt.BOARD_CODE as boardCode , bt.BOARD_TITLE as boardTitle , (SELECT ui.USER_ID FROM USER_INFO ui WHERE ui.USER_CODE = bt.RGSTUS_ID) as rgstusId , TO_CHAR(bt.RGDT_DT, 'YYYY-MM-DD HH24:MI') as rgdtDt , bt.BOARD_COUNT as boardCount FROM BOARD_TB bt WHERE bt.USE_YN = 'Y' AND bt.STUDY_CODE = #{studyCode} AND bt.BOARD_TYPE = '40'
AND bt.RGSTUS_ID IN (SELECT ui2.USER_CODE FROM USER_INFO ui2 WHERE ui2.USE_YN = 'Y' AND ui2.USER_ID LIKE '%' || #{searchBoardRgstusId} || '%' )
AND bt.BOARD_TITLE LIKE '%' || #{searchBoardTitle} || '%'
) main WHERE ROWNUM <= #{last} ) WHERE RNUM >= #{first}

mapper.update:

UPDATE	BOARD_TB
SET		BOARD_COUNT	=	BOARD_COUNT+1
WHERE	BOARD_CODE	=	#{boardCode}


UPDATE 	BOARD_TB
SET		BOARD_TITLE			=	#{boardTitle}
	,	BOARD_DESC			=	#{boardDesc}
	,	UPDT_DT				=	sysdate
WHERE	BOARD_CODE			=	#{boardCode}

mapper.delete:


UPDATE 	BOARD_TB
SET 	USE_YN	=	'N'
WHERE	BOARD_CODE			=	#{boardCode}



DELETE	
FROM	USER_IN_STUDY
WHERE	STUDY_CODE			=	#{studyCode}
	AND	USER_CODE			=	(	SELECT USER_CODE
									FROM USER_INFO
									WHERE USER_ID = #{userCodeTo}
								)

mapper.update:

UPDATE 	STUDY_APPLICATION_FORM_USER
