mapper.select:
SELECT * FROM ( SELECT ROWNUM as RNUM , main.* FROM ( SELECT /*+ ordered use_nl(bt) INDEX_DESC(bt BOARD_IDX ) */ bt.BOARD_CODE as boardCode , bt.BOARD_TITLE as boardTitle , (SELECT ui.USER_ID FROM USER_INFO ui WHERE ui.USER_CODE = bt.RGSTUS_ID) as rgstusId , TO_CHAR(bt.RGDT_DT, 'YYYY-MM-DD HH24:MI') as rgdtDt , bt.BOARD_COUNT as boardCount FROM BOARD_TB bt WHERE bt.USE_YN = 'Y' AND bt.BOARD_TYPE = '10'
AND bt.RGSTUS_ID IN (SELECT ui2.USER_CODE FROM USER_INFO ui2 WHERE ui2.USE_YN = 'Y' AND ui2.USER_ID LIKE '%' || #{searchBoardRgstusId} || '%' )
AND bt.BOARD_TITLE LIKE '%' || #{searchBoardTitle} || '%'
) main WHERE ROWNUM <= #{last} ) WHERE RNUM >= #{first}

mapper.delete:


UPDATE 	BOARD_TB
SET 	USE_YN	=	'N'
WHERE	BOARD_CODE	=	#{boardCode}

mapper.update:

UPDATE 	BOARD_TB
SET		BOARD_TITLE			=	#{boardTitle}
	,	BOARD_DESC			=	#{boardDesc}
	,	UPDT_DT				=	sysdate
WHERE	BOARD_CODE			=	#{boardCode}


UPDATE	BOARD_TB
SET		BOARD_COUNT	=	BOARD_COUNT+1
WHERE	BOARD_CODE	=	#{boardCode}
