mapper.select:
SELECT * FROM ( SELECT ROWNUM as RNUM , main.* FROM ( SELECT /*+ ordered use_nl(sn) INDEX_DESC(sn SYSTEMNOTICE_IDX) */ sn.SYSTEM_NOTICE_CODE as systemNoticeCode , sn.TITLE as systemNoticeTitle , (SELECT ui.USER_ID FROM USER_INFO ui WHERE ui.USER_CODE = sn.RGSTUS_ID) as systemNoticeRgstusId , TO_CHAR(sn.RGDT_DT, 'YYYY-MM-DD HH24:MI') as systemNoticeTime , sn.BOARD_COUNT as boardCount FROM SYSTEM_NOTICE sn WHERE sn.USE_YN = 'Y'
AND sn.RGSTUS_ID IN (SELECT ui2.USER_CODE FROM USER_INFO ui2 WHERE ui2.USE_YN = 'Y' AND ui2.USER_ID LIKE '%' || #{searchSystemNoticeRgstusId} || '%' )
AND sn.TITLE LIKE '%' || #{searchSystemNoticeTitle} || '%'
) main WHERE ROWNUM <= #{last} ) WHERE RNUM >= #{first}

mapper.delete:


